import("./classes/runtime").then(({ render }) => {
  const els = document.querySelectorAll("[data-lpui-component]");

  Array.prototype.forEach.call(els, (el) => {
    const { lpuiComponent: component, lpuiComponentProps: props } = el.dataset;

    render({
      component,
      el,
      props: JSON.parse(props),
    });
  });
});
