import * as cn from "classnames";
import * as React from "react";
import * as styles from "./styles.css";

export interface ISearchInputProps {
  id?: string;
  name?: string;
  className?: string;
  value?: string;
  placeholder?: string;
  required?: boolean;
  onChange?: any;
  innerRef?: any;
}

const SearchInput = React.forwardRef<HTMLInputElement, ISearchInputProps>(({
  id,
  name,
  className,
  value,
  placeholder,
  required,
  onChange,
}: ISearchInputProps, ref) => (
  <input
    id={id}
    name={name}
    value={value}
    className={cn(styles.searchInput, className, "js-gtm-search-input")}
    placeholder={placeholder}
    required={required}
    onChange={onChange}
    ref={ref}
    type="search"
    autoCapitalize="none"
    autoComplete="off"
    autoCorrect="off"
    spellCheck={false}
  />
));

export default SearchInput;
