import formattedPrice from "../utils/formattedPrice";

describe("formattedPrice utility", () => {
  it("return the original price value because it’s an invalid price", () => {
    const price = formattedPrice("$38.082");

    expect(price).toEqual("$38.082");
  });

  it("return the original price value because it’s an invalid price", () => {
    const price = formattedPrice("$38.08.2");

    expect(price).toEqual("$38.08.2");
  });

  it("return the original price value because it’s a valid price", () => {
    const price = formattedPrice("$38.08");

    expect(price).toEqual("$38.08");
  });

  it("return a price value with the decimal and zero removed", () => {
    const price = formattedPrice("$38.0");

    expect(price).toEqual("$38");
  });

  it("return a price value with a zero added", () => {
    const price = formattedPrice("$38.2");

    expect(price).toEqual("$38.20");
  });
});
