// #region imports
import { Container } from "inversify";
import {
  IService,
  IResourceMapper,
} from "../interfaces";
import * as TYPES from "../types";
import UserService from "../resources/user/userService";
import UserMapper from "../resources/user/userMapper";
import User, { IUser, IUserResource } from "../resources/user";
import BookmarkList, { IBookmarkList } from "../resources/bookmarkList";
import BookmarkListService from "../resources/bookmarkList/service";
import Bookmark, { IBookmark } from "../resources/bookmark";
import BookmarkService from "../resources/bookmark/bookmarkService";
import PlaceService, { IPlaceService } from "../resources/place/service";
import PartnerActivityService, { IPartnerActivityService } from "../resources/partnerActivity/service";
import PoiService, { IPoiService } from "../resources/poi/service";
import LodgingService, { ILodgingService } from "../resources/lodging/service";
import AvailabilityService, { IAvailabilityService } from "../resources/availability/service";
import TagService, { ITagService } from "../resources/tag/service";
import SearchService, { ISearchService } from "../resources/search/service";
import { IBlogPostResource, IBlogPost } from "../resources/blogPost";
import BlogPostMapper from "../resources/blogPost/blogPostMapper";
import BlogPostService, { IBlogPostService } from "../resources/blogPost/blogPostService";
import { IBlogCategoryResource, IBlogCategory } from "../resources/blogCategory";
import BlogCategoryMapper from "../resources/blogCategory/blogCategoryMapper";
import BlogCategoryService, { IBlogCategoryService } from "../resources/blogCategory/blogCategoryService";
import BookmarkPopularity, { IBookmarkPopularity } from "../resources/bookmarkPopularity/index";
import BookmarkPopularityService, { IBookmarkPopularityService } from "../resources/bookmarkPopularity/service";
import Association, { IAssociation } from "../resources/association/index";
import AssociationService, { IAssociationService } from "../resources/association/service";
import AssociationEntry, { IAssociationEntry } from "../resources/associationEntry/index";
import AssociationEntryService, { IAssociationEntryService } from "../resources/associationEntry/service";
import VideoService, { IVideoService } from "../resources/video/service";
import Video, { IVideo } from "../resources/video";
import VideoSourceService, { IVideoSourceService } from "../resources/videoSource/service";
import VideoSource, { IVideoSource } from "../resources/videoSource/index";
import VideoPlaylistService, { IVideoPlaylistService } from "../resources/videoPlaylist/service";
import VideoPlaylist, { IVideoPlaylist } from "../resources/videoPlaylist";
import VideoPlaylistItem, { IVideoPlaylistItem } from "../resources/videoPlaylistItem";
import BlacklistItem, { IBlacklistItem } from "../resources/blacklistItem/index";
import BlacklistItemService, { IBlacklistItemService } from "../resources/blacklistItem/service";
import TagAssociation, { ITagAssociation } from "../resources/tagAssociation/index";
import TagAssociationService, { ITagAssociationService } from "../resources/tagAssociation/service";
import ProductService, { IProductService } from "../resources/product/service";
// #endregion

export default (container: Container) => {
  container.bind<IResourceMapper<IBlogPost, IBlogPostResource>>(TYPES.BlogPostMapper).to(BlogPostMapper);
  container.bind<IBlogPostService>(TYPES.BlogPostService).to(BlogPostService);
  container.bind<IResourceMapper<IBlogCategory, IBlogCategoryResource>>(TYPES.BlogCategoryMapper).to(BlogCategoryMapper);
  container.bind<IBlogCategoryService>(TYPES.BlogCategoryService).to(BlogCategoryService);
  container.bind<IBookmark>(TYPES.Bookmark).to(Bookmark);
  container.bind<IBookmarkList>(TYPES.BookmarkList).to(BookmarkList);
  container.bind<IBookmarkPopularityService>(TYPES.BookmarkPopularityService).to(BookmarkPopularityService);
  container.bind<IBookmarkPopularity>(TYPES.BookmarkPopularity).to(BookmarkPopularity);
  container.bind<IBookmark>(TYPES.BookmarkPopularity).to(BookmarkPopularity);
  container.bind<ILodgingService>(TYPES.LodgingService).to(LodgingService);
  container.bind<AvailabilityService>(TYPES.AvailabilityService).to(AvailabilityService);
  container.bind<IPartnerActivityService>(TYPES.PartnerActivityService).to(PartnerActivityService);
  container.bind<IPlaceService>(TYPES.PlaceService).to(PlaceService);
  container.bind<IPoiService>(TYPES.PoiService).to(PoiService);
  container.bind<IResourceMapper<IUser, IUserResource>>(TYPES.UserResourceMapper).to(UserMapper);
  container.bind<IService<IBookmark>>(TYPES.BookmarkService).to(BookmarkService);
  container.bind<IService<IBookmarkList>>(TYPES.BookmarkListService).to(BookmarkListService);
  container.bind<IService<IUser>>(TYPES.UserService).to(UserService);
  container.bind<ITagService>(TYPES.TagService).to(TagService);
  container.bind<ISearchService>(TYPES.SearchService).to(SearchService);
  container.bind<IUser>(TYPES.User).to(User);
  container.bind<IAssociation>(TYPES.Association).to(Association);
  container.bind<IAssociationService>(TYPES.AssociationService).to(AssociationService);
  container.bind<IAssociationEntry>(TYPES.AssociationEntry).to(AssociationEntry);
  container.bind<IAssociationEntryService>(TYPES.AssociationEntryService).to(AssociationEntryService);
  container.bind<IVideo>(TYPES.Video).to(Video);
  container.bind<IVideoService>(TYPES.VideoService).to(VideoService);
  container.bind<IVideoSource>(TYPES.VideoSource).to(VideoSource);
  container.bind<IVideoSourceService>(TYPES.VideoSourceService).to(VideoSourceService);
  container.bind<IVideoPlaylistService>(TYPES.VideoPlaylistService).to(VideoPlaylistService);
  container.bind<IVideoPlaylist>(TYPES.VideoPlaylist).to(VideoPlaylist);
  container.bind<IVideoPlaylistItem>(TYPES.VideoPlaylistItem).to(VideoPlaylistItem);
  container.bind<IBlacklistItem>(TYPES.BlacklistItem).to(BlacklistItem);
  container.bind<IBlacklistItemService>(TYPES.BlacklistItemService).to(BlacklistItemService);
  container.bind<ITagAssociation>(TYPES.TagAssociation).to(TagAssociation);
  container.bind<ITagAssociationService>(TYPES.TagAssociationService).to(TagAssociationService);
  container.bind<IProductService>(TYPES.ProductService).to(ProductService);
};
