import { injectable } from "inversify";
import BaseService from "../../service";
import { IService, IResourceMapper } from "../../interfaces";
import { IAssociation, IAssociationResource } from "./";
import Mapper from "./mapper";

export interface IAssociationService extends IService<IAssociation> {}

@injectable()
export default class AssociationService extends BaseService<IAssociation, IAssociationResource> implements IAssociationService {
  resource: string = "association";
  mapper: IResourceMapper<IAssociation, IAssociationResource> = new Mapper();

  public url() {
    return `${this.host}/associations`;
  }
}
