import { injectable } from "inversify";
import BaseService from "../../service";
import { IService, IResourceMapper } from "../../interfaces";
import { IBlacklistItem, IBlacklistItemResource } from "./";
import Mapper from "./mapper";

export interface IBlacklistItemService extends IService<IBlacklistItem> {}

@injectable()
export default class BlacklistItemService extends BaseService<IBlacklistItem, IBlacklistItemResource> implements IBlacklistItemService {
  resource: string = "blacklist-item";
  mapper: IResourceMapper<IBlacklistItem, IBlacklistItemResource> = new Mapper();

  public url() {
    return `${this.host}/blacklist-items`;
  }
}
