import { inject, injectable } from "inversify";
import BaseService from "../../service";
import { IHttpClient, IService, IResourceMapper, IOpenIdUserInfoResponse, IFindParams } from "../../interfaces";
import { IBlogCategory, IBlogCategoryResource, blogCategoryType } from "../blogCategory";
import * as TYPES from "../../types";

export interface IBlogCategoryService extends IService<IBlogCategory> {}

@injectable()
export default class UserService extends BaseService<IBlogCategory, IBlogCategoryResource> implements IBlogCategoryService {
  public resource: string = blogCategoryType;
  @inject(TYPES.BlogCategoryMapper) public mapper: IResourceMapper<IBlogCategory, IBlogCategoryResource>;

  public url() {
    return `${this.host}/blog-posts/categories`;
  }
}
