import { injectable } from "inversify";
import {
  IJsonApiResponse,
  IJsonApiResource,
} from "../../interfaces";

export const blogCategoryType = "category";

export interface IBlogCategoryAttributes {
  text: string;
  slug: string;
}

export interface IBlogCategoryResource extends IJsonApiResource<IBlogCategoryAttributes> {}
export interface IUserResponse extends IJsonApiResponse<IBlogCategoryResource> {}

export interface IBlogCategory {
  id: string;
  text: string;
  slug: string;
}

@injectable()
export default class BlogCategory implements IBlogCategory {
  id: string;
  text: string;
  slug: string;
}
