import { inject, injectable } from "inversify";
import BaseService from "../../service";
import { IHttpClient, IService, IResourceMapper, IOpenIdUserInfoResponse, IFindParams } from "../../interfaces";
import User, { IBlogPost, IBlogPostResource, blogPostType } from "../blogPost";
import * as TYPES from "../../types";

export interface IBlogPostService extends IService<IBlogPost> {}

@injectable()
export default class UserService extends BaseService<IBlogPost, IBlogPostResource> implements IBlogPostService {
  public resource: string = blogPostType;
  @inject(TYPES.BlogPostMapper) public mapper: IResourceMapper<IBlogPost, IBlogPostResource>;

  public url() {
    return `${this.host}/blog-posts`;
  }
}
