type WordForm =  "plural" | "singular";

const singular: WordForm = "singular";
const plural: WordForm = "plural";

export const isTourOrActivity = (
  duration: { unit: string; duration: number },
  type: WordForm = singular,
) => {
  if (!duration) {
    return "";
  }
  if (duration.unit !== "days") {
    return type === singular ? "activity" : "activities";
  }

  if (duration.unit === "days" && duration.duration > 3) {
    return type === singular ? "tour" : "tours";
  }

  return type === singular ? "activity" : "activities";
};
