import { injectable } from "inversify";
import BaseService from "../../service";
import { IService, IResourceMapper, IFindParams } from "../../interfaces";
import { IProduct, IProductResource, IProductResponse, productType } from "./";
import Mapper from "./mapper";

export interface IProductService extends IService<IProduct> {}

@injectable()
export default class ProductService extends BaseService<IProduct, IProductResource> implements IProductService {
  resource: string = productType;
  mapper: IResourceMapper<IProduct, IProductResource> = new Mapper();

  public url() {
    return `${this.host}/products`;
  }
}
