import {ITopic, ITopicResource, Topic} from "./index";
import Mapper from "../../mapper";
import {IJsonApiResource} from "../../interfaces";

export default class TopicMapper extends Mapper<ITopic, ITopicResource> {
  toModel(resource: ITopicResource, included?: IJsonApiResource[]): ITopic {
    const model = new Topic();

    model.id = resource.id;
    model.label = resource.attributes.label;

    return model;
  }

  toResource(model: ITopic): ITopicResource {
    return {
      id: model.id,
      type: "topic",
      attributes: {
        label: model.label,
      },
      relationships: {}
    };
  }
}
