import { injectable } from "inversify";
import BaseService from "../../service";
import { IService, IResourceMapper } from "../../interfaces";
import Mapper from "./mapper";
import { IVideo, IVideoResource, videoType } from "./index";

export interface IVideoService extends IService<IVideo> {}

@injectable()
export default class AssociationService extends BaseService<IVideo, IVideoResource> implements IVideoService {
  resource: string = videoType;
  mapper: IResourceMapper<IVideo, IVideoResource> = new Mapper();

  public url() {
    return `${this.host}/videos`;
  }
}
