import { injectable } from "inversify";
import BaseService from "../../service";
import { IService, IResourceMapper } from "../../interfaces";
import Mapper from "./mapper";
import {IVideoPlaylist, IVideoPlaylistResource, videoPlaylistType} from "./index";

export interface IVideoPlaylistService extends IService<IVideoPlaylist> {}

@injectable()
export default class VideoPlaylistService extends BaseService<IVideoPlaylist, IVideoPlaylistResource>
  implements IVideoPlaylistService {

  resource: string = videoPlaylistType;
  mapper: IResourceMapper<IVideoPlaylist, IVideoPlaylistResource> = new Mapper();

  public url() {
    return `${this.host}/videos/playlists`;
  }
}
