const removeDecimalAndZeroFromPrice = (price) => price.replace(".0", "");
const addZeroToPrice = (price) => price + "0";

export default function fixFormattedPrice(price) {
  const priceSplit = price.split(".");

  if(priceSplit[1]){
    if (priceSplit[1].length === 1 && priceSplit[1] === "0") {
      return removeDecimalAndZeroFromPrice(price);
    }

    if (priceSplit[1].length === 1) {
      return addZeroToPrice(price);
    }
  }
 

  return price;
}
